'use strict';

/**
 * Sum two numbers
 * @param {a} number
 * @param {b} number
 * @return {number}
 */
module.exports.sumNumbers = function(a, b) {
  return a + b;
}


/**
 * Multiply two numbers
 * @param {a} number
 * @param {b} number
 * @return {number}
 */
module.exports.multiplyNumbers = function(a, b) {
    return a * b;
}