'use strict';

var expect = require('chai').expect;
var math = require('../index');

var sumNumbers = math.sumNumbers;
var multiplyNumbers = math.multiplyNumbers;

describe('#sumNumbers', function() {
    function makeTest(a, b) {
        var expected = a + b;
        it("The sum of two numbers: " + a + ", " + b + " = "  + expected, function() {
            expect(sumNumbers(a, b)).to.equal(expected);
        });
    }

    for (var x = 1; x <= 5; x++) {
        makeTest(x, x - 10);
    }
});


describe('#multiplyNumbers', function() {
    function makeTest(a, b) {
        var expected = a * b;
        it("The multiplication of two numbers: " + a + ", " + b + " = "  + expected, function() {
            expect(multiplyNumbers(a, b)).to.equal(expected);
        });
    }

    for (var x = 1; x <= 5; x++) {
        makeTest(x, x - 10);
    }
});
